    private void botaoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_botaoActionPerformed
        // TODO add your handling code here:
        Double kids1 = Double.parseDouble(texto1.getText()) * 15.50;
        Double kids2 = Double.parseDouble(texto2.getText()) * 10.00;
        Double kids3 = Double.parseDouble(texto3.getText()) * 5.50;
        Double resulFinal = kids1 + kids2 + kids3;
        resultado.setText(String.format("Sua bolsa kids ser√° de R$%.2f", resulFinal));
    }//GEN-LAST:event_botaoActionPerformed

